import base64
import os

# Create output directory
output_dir = "backend/data/images/granular"
os.makedirs(output_dir, exist_ok=True)

# Extracted base64 data from browser subagent (Q1 - 5 options only, no main image for this question type)
images = [
    {"filename": "nvr_q1_opt_a.png", "content_b64": ""},
    {"filename": "nvr_q1_opt_b.png", "content_b64": ""},
    {"filename": "nvr_q1_opt_c.png", "content_b64": ""},
    {"filename": "nvr_q1_opt_d.png", "content_b64": ""},
    {"filename": "nvr_q1_opt_e.png", "content_b64": ""}
]

# Save each image
for img in images:
    filepath = os.path.join(output_dir, img["filename"])
    with open(filepath, "wb") as f:
        f.write(base64.b64decode(img["content_b64"]))
    print(f"Saved: {filepath}")

print(f"\nTotal images saved: {len(images)}")
